(*
  For each @ I need the surronding eight squares
    i.e. I need the line above and the line below
    so when I am going through, I should have three - two lines in there
    top middle and bot and count it in a square around it

  The part 2 is probs just recursive calls of it where I remove the ones I can access and continue
    so might plan for that but idk
*)
let array_char s =
  Array.init (String.length s) (String.get s)

let checkRight top mid bot idx topd botd =
  (if topd && idx+1 < Array.length top && top.(idx+1) = '@' then 1 else 0) +
  (if idx+1 < Array.length mid && mid.(idx+1) = '@' then 1 else 0) +
  (if botd && idx+1 < Array.length bot && bot.(idx+1) = '@' then 1 else 0)

let checkLeft top mid bot idx topd botd =
  (if topd && idx-1 >= 0 && top.(idx-1) = '@' then 1 else 0) +
  (if idx-1 >= 0 && mid.(idx-1) = '@' then 1 else 0) +
  (if botd && idx-1 >= 0 && bot.(idx-1) = '@' then 1 else 0)

let checkMid   top mid bot idx topd botd =
  (if topd && top.(idx) = '@' then 1 else 0) +
  (if botd && bot.(idx) = '@' then 1 else 0)

let solver arr p =
  let lines = Array.length arr in
  let lineLength =
    if lines = 0 then failwith "empty list"
    else Array.length arr.(0)
  in
  let rec solveLine top mid bot ldx acc =
    let topL = Array.length top <> 0 in
    let botL = Array.length bot <> 0 in
    let rec solveSquare idx acc =
      if idx >= lineLength then
        if ldx >= lines then acc (* last line *)
        else (* switch lines *)
          let top = mid in
          let mid = bot in
          let bot = if ldx + 1 >= lines then [||] else arr.(ldx+1) in
          solveLine top mid bot (ldx+1) acc
      else
        if mid.(idx) <> '@' then solveSquare (idx+1) acc else
          let count = checkLeft  top mid bot idx (topL) (botL) +
             checkMid   top mid bot idx (topL) (botL) +
             checkRight top mid bot idx (topL) (botL)
          in if count >= 4 then solveSquare (idx+1) acc
          else (
            (if p = 2 then arr.(ldx-1).(idx) <- '.' else ());
            solveSquare (idx+1) (acc+1)
          )
      in
      solveSquare 0 acc
  in
  (* Assuming the grid is more than 1 line *)
  if Array.length arr < 2 then failwith "weird starting list"
  else solveLine [||] arr.(0) arr.(1) 1 0

let solve ls = 
  let arr = Array.map array_char (Array.of_list ls) in
  solver arr 1

let solve2 ls =
  let arr = Array.map array_char (Array.of_list ls) in
  let rec helper arr acc =
    let solved  = solver arr 2 in
    if solved = 0 then acc else helper arr (solved + acc)
  in helper arr (solver arr 2)

